parameters.jsCode:
// Define servers to monitor
const servers = [
  { name: 'Web Server 1', url: 'https://api.company.com/health', type: 'web' },
  { name: 'Database Server', url: 'https://db.company.com/status', type: 'database' },
  { name: 'Redis Cache', url: 'https://redis.company.com/ping', type: 'cache' },
  { name: 'Elasticsearch', url: 'https://search.company.com/_cluster/health', type: 'search' },
  { name: 'File Server', url: 'https://files.company.com/status', type: 'storage' }
];

return servers.map(server => ({ json: server }));

// Analyze server response and determine health status
const server = $('Define Servers').item.json;
const response = $input.first();

const startTime = Date.now();
let status = 'healthy';
let issues = [];
let responseTime = 0;

try {
  const responseData = response.json;
  responseTime = response.headers?.['x-response-time'] || 0;

  // Check HTTP status
  if (response.statusCode >= 400) {
    status = 'unhealthy';
    issues.push(`HTTP ${response.statusCode} error`);
  }

  // Check response time
  if (responseTime > 5000) {
    status = 'degraded';
    issues.push('High response time');
  }

  // Server-specific health checks
  if (server.type === 'database') {
    if (responseData.connections?.active > 80) {
      status = 'warning';
      issues.push('High database connections');
    }
  }

  if (server.type === 'web') {
    if (responseData.cpu_usage > 90) {
      status = 'critical';
      issues.push('High CPU usage');
    }
    if (responseData.memory_usage > 85) {
      status = 'warning';
      issues.push('High memory usage');
    }
  }

} catch (error) {
  status = 'unhealthy';
  issues.push('Connection failed or invalid response');
}

return [{
  json: {
    server_name: server.name,
    server_type: server.type,
    url: server.url,
    status: status,
    response_time: responseTime,
    issues: issues,
    timestamp: new Date().toISOString(),
    raw_response: $input.first().json
  }
}];

