const lead = $input.first().json;
return [{
  json: {
    nome: lead.nome || lead.first_name || '',
    email: lead.email || '',
    telefono: lead.telefono || lead.phone || '',
    interesse: lead.interesse || lead.product || '',
    fonte: lead.fonte || 'form-web',
    timestamp: new Date().toISOString()
  }
}];

